import React, {useState, useEffect, useCallback} from 'react';
import './App.css';
import './assets/styles/style.css';
import {AnswersList, Chats} from "./components/index"
import defaultDataset from "./dataset" //データベースの値をこちらでインポートする


// クラスコンポーネントで一度作成する
export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      answers: [],
      chats: [],
      currentID: "init",
      dataset: defaultDataset,
    };
  }

  // 回答クリック時のメソット
  selectAnswer = (selectAnswer, nextQuestionId) => {
    // 選択肢は複数あるので、switch文での記述を行う
     switch(true){
        case nextQuestionId === "init":
          // initのだった場合の処理を記述する
        default:
          // クリックされた値をchatsに追加する
          chat = {
            text: selectAnswer,
            type: "answer"
          };

          const chats = this.state.chats
     }
  };

  // 初期のチャットを入れていく

  initChats() {
    const initDataset = this.state.dataset[this.state.currentID];
    const chat = {
      text: initDataset.question,
      type: "question",
    };
    const chats = this.state.chats;
    chats.push(chat);

    this.setState({
      chats: chats,
    });
  }

  // 初期の回答と質問を入力するメソットを作成する
  initAnswer = () => {
    const initDataset = this.state.dataset[this.state.currentID]; //初期値のオブジェクト取得
    const initAnswers = initDataset.answers; // オブジェクト配列が格納されている
    this.setState({
      answers: initAnswers,
    });
  };

  // renderが走った後に呼び出されるメソット
  componentDidMount() {
    this.initAnswer();
    this.initChats();
  }

  render() {
    return (
      <section className="c-section">
        <div className="c-box">
          <Chats chats={this.state.chats} />
          <AnswersList answers={this.state.answers} />
        </div>
      </section>
    );
  }
}
