import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import ListItem from "@material-ui/core/ListItem";
import ListItemAvatar from "@material-ui/core/ListItemAvatar";
import Avatar from "@material-ui/core/Avatar";
import Masl from '../assets/imgae/istockphoto.jpg'
import Noimage from '../assets/imgae/no-profile'



const useStyles = makeStyles((theme) => ({
  root: {
    width: "100%",
    maxWidth: "36ch",
    backgroundColor: theme.palette.background.paper,
  },
  inline: {
    display: "inline",
  },
}));


const Chat = (props) => {
  const isQuestion = (props.type === "question"); // questionかanswerかの判別
  const classes = isQuestion ? "p-chat__row" : "p-chat__reverse"


  return (
    <ListItem alignItems="flex-start" className={classes}>
      <ListItemAvatar>
        {isQuestion ? (
          <Avatar alt="Remy Sharp" src={Masl} />
        ) : (
          <Avatar alt="Remy Sharp" src={Noimage} />
        )}
      </ListItemAvatar>
      <div className="p-chat__bubble">{props.text}</div>
    </ListItem>
  );
};

export default Chat;
