import React, {useState, useEffect, useCallback} from 'react';
import './App.css';
import './assets/styles/style.css';
import {AnswersList, Chats} from "./components/index"
// import defaultDataset from "./dataset" 
//データベースの値をこちらでインポートする
import { db } from "./firebase/index";


export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      answers: [],
      chats: [],
      currentID: "init",
      dataset: {},
      open: false,
    };

    this.selectAnswer = this.selectAnswer.bind(this);
  }

  displayNextQuestion = (nextQuestionId) => {
    const chats = this.state.chats;
    chats.push({
      text: this.state.dataset[nextQuestionId].question,
      type: "question",
    });

    this.setState({
      answers: this.state.dataset[nextQuestionId].answers,
      chats: chats,
      currentID: nextQuestionId,
    });
  };

  selectAnswer = (selectAnswer, nextQuestionId) => {
    switch (true) {
      case nextQuestionId === "init":
        this.displayNextQuestion(nextQuestionId);
        break;

      // urlできた場合の実装
      case /^https:*/.test(nextQuestionId):
        const a = document.createElement("a");
        a.href = nextQuestionId;
        a.target = "_blank";
        a.click();

      default:
        const chat = {
          text: selectAnswer,
          type: "answer",
        };

        const chats = this.state.chats;
        chats.push(chat);

        this.setState({
          chats: chats,
        });
        // 遅延時間を使って回答するようにする(2つの引数を取れる)
        setTimeout(() => this.displayNextQuestion(nextQuestionId), 500);

        break;
    }
  };

  initChats() {
    const initDataset = this.state.dataset[this.state.currentID];
    const chat = {
      text: initDataset.question,
      type: "question",
    };
    const chats = this.state.chats;
    chats.push(chat);

    this.setState({
      chats: chats,
    });
  }

  // datasetの値を変更するメソット
  initDataset(dataset) {
    this.setState({
      dataset: dataset,
    });
  }

componentDidMount() {

    //  非同期処理を行う
  (async ()=>{
      const dataset = this.state.dataset; // 空のデータベースのstateを取得する
      await db.collection('questions').get().then((snapshots) =>{
          snapshots.forEach((doc)=>{
            const id = doc.id // データベースないのキーの値
            const data = doc.data(); // キーの値の要素全てがこちらに格納される
            dataset[id] = data; // 値がどんどんキーと共に格納されていく     
          })

          this.initDataset(dataset);
          const initAnswer = "";
      })
  })();
  }

  // スクロール時の挙動
  componentDidUpdate() {
    const scrollAre = document.getElementById("scrollarea");
    if (scrollAre) {
      scrollAre.scrollTop = scrollAre.scrollHeight;
      console.log(scrollAre.scrollTop);
      console.log(scrollAre.scrollHeight);
    }
  }

  render() {
    return (
      <section className="c-section">
        <div className="c-box">
          <Chats chats={this.state.chats} />
          <AnswersList
            answers={this.state.answers}
            select={this.selectAnswer}
          />
        </div>
      </section>
    );
  }
}
