import React, {useState, useEffect, useCallback} from 'react';
import './App.css';
import './assets/styles/style.css';
import {AnswersList, Chats} from "./components/index"

import {dataset} from "./dataset"


// 関数コンポーネントで作成する
const App = ()=>{
    const [answers, serAnswers] = useState([]); // 回答を表示するコンポーネント
    const [chats, setChats] = useState([]);  //  チャットに表示するコンポーネント
    const [currentID, setCurrentId] = useState("init"); //現在の質問ID
    const [dataset, setDataset] = useState({}); //質問と回答のデータセット
    const [open, setOpen] = useState(false); //お問い合わせフォームのモーダル

    displayNextQuestion = (nextQuestionId) => {
      const chats = this.state.chats;
      chats.push({
        text: this.state.dataset[nextQuestionId].question,
        type: "question",
      });

      this.setState({
        answers: this.state.dataset[nextQuestionId].answers,
        chats: chats,
        currentID: nextQuestionId,
      });
    };

    selectAnswer = (selectAnswer, nextQuestionId) => {
      switch (true) {
        case nextQuestionId === "init":
          this.displayNextQuestion(nextQuestionId);
          break;
        // urlできた場合の実装
        case /^https:*/.test(nextQuestionId):
          const a = document.createElement("a");
          a.href = nextQuestionId;
          a.target = "_blank";
          a.click();

        // contactだった場合にもーだるウィ開く
        case nextQuestionId === "contact":
          this.handleClickOpen();
          break;

        default:
          const chat = {
            text: selectAnswer,
            type: "answer",
          };

          const chats = this.state.chats;
          chats.push(chat);

          this.setState({
            chats: chats,
          });
          // 遅延時間を使って回答するようにする(2つの引数を取れる)
          setTimeout(() => this.displayNextQuestion(nextQuestionId), 500);

          break;
      }
    };


    return(
        <section className="c-section">
            <div className="c-box">      
                <Chats chats={chats}/>   
                <AnswersList answers={answers}/>          
            </div>
        </section>
    )
}


export default App;
